INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (3111401, '斯琴高娃,高明,吕凉', '中国大陆', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2375004152.webp', '一个看似平常的工作日，女检察长叶子菁突然接到老领导市人大主任陈汉杰的一个电话，责令她和检察院立即传讯钟楼区公安局副局长王小峰。此前，检察院已就王小峰收受赃车、涉黄护黑一案，按市人大的要求作了调查……', '蒋绍华', '周梅森', null, '汉语普通话', '国家公诉');
