INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26373370, 'Michelle Dockery', '英国', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2426399326.webp', '本系列纪录片由Michelle Dockery作旁白。从常年冰雪覆盖的山峰到温暖的亚热带气候，日本的自然景观非常丰富多彩。这些岛屿上生活着许许多多的野生动物；而且为了适应各种极端的环境，这些动物和人类的生活经常交织在一起。 　　BBC这次的日本生态之旅重点放在了人与动物的关系上，风格上类似于人类星球那种猎奇拍法，探寻日本众多岛屿中那些特殊环境下的不为人知的生命故事。', 'Gavin Maxwell', null, '纪录片', '英语', '狂野日本');
INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26374197, '杰克·约翰逊,沙梅克·摩尔,海莉·斯坦菲尔德', '美国', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2535851348.webp', '正就读中学的少年迈尔斯（沙梅克·摩尔 Shameik Moore 配音）意外被一只遭受放射性污染的蜘蛛咬伤，随后身体发生奇特的变化。偶然机缘，他目睹了蜘蛛侠（克里斯·派恩 Chris Pine 配音）和犯罪头目金并（列维·施瑞博尔 Liev Schreiber 配音）的战斗。为了阻止金并利用时空对撞机扰乱宇宙，蜘蛛侠最终壮烈牺牲。可未过多久，来自平行宇宙的颓废的彼德·帕克（杰克·约翰逊 Jake ...', '彼得·拉姆齐,罗德尼·罗斯曼,鲍勃·佩尔西凯蒂', '罗德尼·罗斯曼,菲尔·罗德,布莱恩·本迪斯', '科幻,动画,动作', '西班牙语,英语', '蜘蛛侠：平行宇宙');
INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26375073, '桑雅·杰哈特,西尔维斯特·格罗特,亚历山大·拜尔', '德国', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2243613354.webp', '德国83年是一部扣人心弦的电视剧，其故事反映的是80年代的德国政治斗争与真实现代战争之间的故事。该剧讲述的是一个土生土长在东德的24岁年轻人马丁·劳赫，被强制派往西德做民主德国国家安全局的秘密间谍。隐姓埋名于西德陆军之中，他必须搜集所有关于北约军事战略的机密。万事开头难，进展并非像他想象那样顺利，他所遇到的每一个人都深藏不可告人的秘密……', 'Samira Radsi,爱德华·贝尔格', 'Anna Winger', '剧情', '德语', '德国八三年');
