INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26550173, null, '中国大陆', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2256432028.webp', '《熊出没之冬日乐翻天》延续《熊出没》、《熊出没之环球大冒险》和《熊出没之丛林总动员》、《熊出没之春日对对碰》轻松搞笑的风格，讲述在冬天的森林里,熊大熊二和光头强之间发生的和冬季有关的系列故事，如冰上钓鱼、打雪仗、做冰雕等，为了阻止光头强破坏森林，保卫自己的家园，以熊大熊二为首的丛林动物们和光头强继续展开保卫森林的斗争。', '丁亮', null, '动画', '汉语普通话', '熊出没之冬日乐翻天');
