INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26680423, '安宁,杨羽,邵庄', '中国大陆', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2617839432.webp', '导演李洪绸以及主演小宝均在微博上表示《毛骗》会有大电影版，但主演邵庄在巡回宣传的时候曾透露有可能会为了票房考虑换人演。 　　终结篇留下的伏笔将在电影版一一揭晓，毕晨曦到底会做出什么选择？她又会不会为小宝他们带来困扰？最后作为彩蛋出现的赵宁是不是会回归？小宝一伙人会不会再次重聚？邵庄烧掉《御世制人录》时地下伸出的那只手又是何意？敬请期待《毛骗.电影版》。', '邢冬冬,李洪绸', '李洪绸', '剧情,犯罪,悬疑', '汉语普通话', '毛骗');
