INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26969075, '一禅小和尚,师父', '中国大陆', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2424675709.webp', '讲述了小和尚一禅和师父以及身边各种人和事，并从中领悟人生的道理，领会身处尘世的芸芸众生的喜怒哀乐，怨憎会爱别离与求不得。用小和尚的视角看世界，用老和尚的话语去阐述人生的各种道理。', '一禅小和尚', '一禅小和尚', '短片,动画', '汉语普通话', '一禅小和尚');
