INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (28510401, '西蒙·佩吉,汉娜·卡利克-布朗,麦茜·理查森·塞勒斯', '英国', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2875400215.webp', '马克·里朗斯、西蒙·佩吉、艾德里安·莱斯特将主演Peacock和Channel 4联合开发的剧集《暗战》。该剧共6集，由彼得·考斯明斯金(《狼厅》)自编自导，故事发生在疫情结束后的2024年，故事背景是即将举行的英国大选，以及即将诞生的英国首位黑人保守党首相(莱斯特饰)。位于英国国家安全局式间谍机构GCHQ核心的一支领衔分析师团队，试图抵御对该国选举系统的网络攻击。', '彼得·考斯明斯金', '彼得·考斯明斯金,亚当·帕特森,德克兰·朗', '剧情', '英语', '暗战 第一季');
