INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (30370906, '赵菲,储小蕾', '中国大陆', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2580949608.webp', '1937年12月，日军攻克南京，制造了“南京大剧照剧照(2张) 屠杀”惨案。为实现“三个月亡华”的野心，日军进攻武汉。我军地下党员、国军中校古剑锋在执行刺杀冈村宁次的任务中牺牲。其妻白浪受组织委派，带队营救从南京逃到武汉的握有南京大屠杀影像资料的两个美国医生，经过与日军特务机关生死较量，圆满完成任务。白浪小队又先后接受了除掉日军驻武汉总司令并夺回国宝、粉碎日军与国民党投降派谈判阴谋、炸毁日军大规模...', '于立清', '老旦,小笔', '剧情,历史,战争', '汉语普通话', '刀尖舞者');
