INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (34813597, '苏扬', '中国大陆', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2567767640.webp', '一条川江，世界上最危险的航道。一个英国人沿江而上，寻找新的商业市场。一座古城在不断涌来的潮水中，已经听见扣门声响。这是发生在19世纪末的一场角力。一方是大清朝廷以及重庆的黎民百姓，另一方是野心勃勃的英国冒险家。', '徐蓓', null, '纪录片', '汉语普通话', '城门几丈高');
