INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35480033, null, '墨西哥', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2654723523.webp', '《我们就是如此》改编自普利策奖获奖记者金杰·汤普森撰写的争议性调查文章，以富有想象力的方式再现了大屠杀发生前数日阿连德小镇居民的生活。本剧由詹姆斯·夏慕斯担任创剧人，并由墨西哥的莫妮卡·雷维利亚和费尔南达·梅尔乔共同担任编剧。《我们就是如此》从受害者的角度讲述了毒品战争的故事。在没有任何预警的情况下，美国缉毒局的行动失误在小镇上引发悲剧，受害者的生活从此被彻底改变。', '阿尔瓦罗·柯里尔,玛丽安娜·舍尼洛', '费尔南达·梅尔乔,詹姆斯·夏慕斯,莫妮卡·雷维利亚', '剧情', '西班牙语', '我们就是如此');
