INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35653164, '金荷娜,李慧英,金成铃', '韩国', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2867248538.webp', '金荷娜在剧中饰演时尚推销员雨贤，虽然以亲民的形象保持着不错的业绩，但却对一切都停滞的状况很烦恼。 　　该剧将以“雨贤”为中心描写网络购物的现况。 　　《婚词离曲》柳正俊导演执导，《善岩女高侦探团》申光浩编剧执笔。', '柳正俊', '申光浩', '剧情', '韩语', '超高跟');
INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35654350, null, '日本', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2847467817.webp', '两名素不相识的人通过试镜后，将演出日记中简单写出的桥段，在只有粗略大纲的情况下完成一部爱情故事。在按照日记指示行动的过程中，他们能够自由展开对话，最终，两人共同经历的一切将影响他们对彼此的看法。观众会忍不住想，演员们会就此假戏真做，真的相恋吗？本剧翻拍自在 1998 到 2002 年间轰动一时的传奇真人秀系列。', null, null, '真人秀', '日语', '未来日记');
INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35656136, '董璇,宋丹丹,王传君', '中国大陆', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2874271435.webp', '《五十公里桃花坞2》是一档生活类社交观察真人秀。本季邀请15位嘉宾来到距离城市中心50公里的海边桃花坞，共同生活21天。节目将探寻在不同社交距离的变化下，当代社会群居生活的多种面貌，并书写美好群居生活的新答案。', '谢涤葵,池源', '周楚囡', '真人秀', '汉语普通话', '五十公里桃花坞2');
INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35656819, '山田杏奈,长尾谦杜,八木莉可子', '日本', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2867146286.webp', '本片是Amazon制作的第一部日本电影，故事改编自森绘都的小说《色彩缤纷》。主人公小白虽然死过一次，但自称“管理员”的神秘人物告诉他，要借用同样死去的高中生小林真的身体“寄宿”。而且也宣告了如果不能在100天内找到真死的原因，真正的死亡会在真正的家人和朋友发现真实身份的时候到来。小白在与真正的青梅竹马藤枝晶、秘密憧憬的前辈高坂美月等人的关系中，逐渐接近真正的死亡真相。', '濑田夏希', '菅野友恵,大浦光太', null, '日语', '借来的100天');
