INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35663416, '方东昇', '中国香港', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2873293608.webp', '茫茫人海，大海捞针，《寻人记》团队齐心合力，继续寻觅。当年街童去哪儿？「带子洪郎」身在何方？「投诉王」又有新冤情？明星保母已经退隐江湖？访队四出寻人，也不时「被寻人」。有当事人主动联络见面，也有观众来信求助，更有受访者委托越洋寻亲……人生就是寻寻觅觅，寻寻觅觅要花很多气 力，往往找不到，偶尔寻到宝。《寻人记II》和大家一起见证成果，体验「白果」，尝尽人生百味，寻回更多「人‧情‧味」。', null, null, '纪录片', '粤语', '寻人记II');
