INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26373370, 'Michelle Dockery', '英国', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2426399326.webp', '本系列纪录片由Michelle Dockery作旁白。从常年冰雪覆盖的山峰到温暖的亚热带气候，日本的自然景观非常丰富多彩。这些岛屿上生活着许许多多的野生动物；而且为了适应各种极端的环境，这些动物和人类的生活经常交织在一起。 　　BBC这次的日本生态之旅重点放在了人与动物的关系上，风格上类似于人类星球那种猎奇拍法，探寻日本众多岛屿中那些特殊环境下的不为人知的生命故事。', 'Gavin Maxwell', null, '纪录片', '英语', '狂野日本');
