INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (26647224, '金所泫,严基俊,李旼赫', '韩国', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2323955139.webp', '《噩梦老师》是一部学园神秘剧。剧中，某所高中日复一日的生活中，学生们在做梦的瞬间陷入了陌生而奇妙、无法抗拒的甜蜜诱惑。李旼赫饰演的高二学生尚宇很有好奇心和正义感，与好友誉林(金所炫 饰)一起，调查身份不明的噩梦老师河奉九(严基俊 饰)调任来校后发生的可疑事件。', '玄文燮', '郑有锡', '剧情,悬疑,惊悚', '韩语', '噩梦老师');
