INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (34800433, '安娜丽莎·科克伦,巴雷特·卡纳汗,梅丽莎·科拉佐', '美国', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2694637488.webp', '改编自Karen M. McManus的畅销小说，讲述五个学生一起被留堂，却只有四个人活着走出教室，预告中的画外音讲述者Simon死去了，另外四个人中有一个人引发了Simon的死，而“没有一个人是完全无辜的”。', '詹妮弗·莫里森', 'Karen M. McManus,Erica Saleh', '剧情,犯罪,悬疑', '英语', '我们有人在撒谎 第一季');
