INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35131210, '张开泰,郭子凡,张籽沐', '中国大陆', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2888419295.webp', '剧集聚焦于北京胡同里一个普通的四合院，三组平凡家庭的温暖日常，以三位少年的青春成长，懵懂情愫为故事主线，在青春底色之上，描摹生活烟火气与成长中的喜怒哀乐，折射出时代发展的脉动。一个四合院，三位少年、三组家庭，讲述了谢乔、秦川、何筱舟三位少年初高中时代共同成长的故事。以发小友情、学习压力、热血竞技、真挚情谊、青春叛逆、家庭冲突这些专属青春的符号奏响一曲少年们的交响乐。', '王雷,刘畅', '九夜茴,吴海求,周洋', '剧情,爱情,家庭', '汉语普通话', '曾少年之小时候');
