INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (35740820, '欧阳伟豪', '中国香港', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2858303527.webp', '在传统港式家庭的成长环境中，或许从小到大，大家对「性」避而不谈，犹如一个「禁忌话题」。有见及此，欧阳伟豪(Ben Sir)带领一班年轻新世代，包括黄紫恩、张盈悦、姜嘉琳、胡敏芝、黄婧灵(波波)、吴兆麟于节目中公开讨论感觉难以启齿的性话题，也会「开箱」性用具、解构性用词，并从专业角度解读正确的性知识，让观众在多元社会中可以互相尊重。', null, null, '真人秀', '粤语', '冇人性教育');
