INSERT INTO PUBLIC.MOVIE (ID, ACTORS, COUNTRY, COVER, DESCRIPTION, DIRECTORS, EDITORS, GENRE, LANGUAGE, NAME) VALUES (36362655, 'Nada El Belkasmi,Adele James,Craig Russell', '英国', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2891135738.webp', '监制贾达·萍克·史密斯带来的这部纪录片剧集探索极具影响力的著名非洲女王的生活。这一季的主角是埃及艳后，她是世界上最著名、最有权势但也蒙受最多误解的女性，她是一位大胆的女王，她的美丽和风流韵事掩盖了她真正的财富：她的智慧。克丽奥佩脱拉的影响一直是许多学术争论的主题，而好莱坞往往忽略了这一点。现在，我们的纪录片剧集将重新审视她人生故事中这一引人入胜的部分。', 'Tina Gharavi,Victoria Adeola Thomas', null, '纪录片', '英语', '埃及艳后');
